query:
select a.customer as "Customer::150",a.territory as "Territory::150",a.division_group as "Division Group::100",a.retail_group as "Retail Group::100",sum(a.net_amount) as "Amount:Float:100",sum(a.qty) as "Quantity",a.sales_person as "Sales Person::120",a.commission_rate as "commission Rate::100",sum(incentives) as "Incentives:Float:100"
from (
SELECT  dt.customer, dt.territory, it.division_group, it.retail_group, dt.transaction_date as posting_date,sot.net_amount,sot.qty, st.commission_rate,st.sales_person, st.allocated_percentage, st.allocated_amount, st.incentives
	from `tabSales Order` dt inner join `tabSales Team` st on dt.name = st.parent inner join `tabSales Order Item` sot on dt.name = sot.parent left join `tabItem` it on sot.item_code = it.item_code
	where st.parenttype = 'Sales Order' and dt.docstatus = 1) a group by a.customer,a.territory,a.division_group,a.retail_group,a.sales_person order by a.sales_person asc;
